mapper.select:
SELECT 

FROM fyjf_t_company a

WHERE a.id = #{id}

SELECT
FROM fyjf_t_company a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM fyjf_t_company a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
from fyjf_t_office of LEFT JOIN fyjf_t_company a on of.company_id = a.id
a.del_flag = '0' and of.id= #{officeId}

mapper.update:
UPDATE fyjf_t_company SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
